n                \"key\":\"scriptManages\",\n                \"type\":\"SubMenu\",\n                \"path\":\"/scriptManages\",\n                \"children\":[\n                    {\n                        \"title\":\"测试脚本\",\n                        \"key\":\"scriptManage\",\n                        \"type\":\"Item\",\n                        \"path\":\"/scriptManage\",\n                        \"children\":[\n                            {\n                                \"title\":\"脚本配置\",\n                                \"key\":\"scriptManage\",\n                                \"type\":\"NoMenu\",\n                                \"path\":\"/scriptManage/scriptConfig\"\n                            },\n                            {\n                                \"key\":\"scriptManage\",\n                                \"title\":\"脚本调试详情\",\n                                \"type\":\"NoMenu\",\n                                \"path\":\"/scriptManage/scriptDebugDetail\"\n                            }\n                        ]\n                    },\n                    {\n                        \"title\":\"运维脚本\",\n                        \"type\":\"Item\",\n                        \"key\":\"scriptOperation\",\n                        \"path\":\"/scriptOperation\"\n                    }\n                ]\n            },\n            {\n                \"title\":\"调试工具\",\n                \"key\":\"debugTool\",\n                \"path\":\"/debugTool\",\n                \"type\":\"SubMenu\",\n                \"children\":[\n                    {\n                        \"title\":\"链路调试\",\n                        \"type\":\"Item\",\n                        \"key\":\"debugTool_linkDebug\",\n                        \"path\":\"/pro/debugTool/linkDebug\",\n                        \"children\":[\n                            {\n                                \"title\":\"链路调试详情\",\n                                \"type\":\"NoMenu\",\n                                \"key\":\"debugTool_linkDebug\",\n                                \"path\":\"/pro/debugTool/linkDebug/detail\"\n                            }\n                        ]\n                    }\n                ]\n            },\n            {\n                \"title\":\"数据源管理\",\n                \"type\":\"SubMenu\",\n                \"path\":\"/dataSourceManage\",\n                \"children\":[\n                    {\n                        \"title\":\"数据源配置\",\n                        \"type\":\"Item\",\n                        \"key\":\"configCenter_dataSourceConfig\",\n                        \"path\":\"/configCenter/dataSourceConfig\"\n                    }\n                ]\n            }\n        ]\n    },\n    {\n        \"title\":\"压测平台\",\n        \"icon\":\"hourglass\",\n        \"path\":\"/hourglass\",\n        \"type\":\"SubMenu\",\n        \"children\":[\n            {\n                \"title\":\"压测管理\",\n                \"type\":\"SubMenu\",\n                \"path\":\"/pressureTestManage\",\n                \"children\":[\n                    {\n                        \"title\":\"压测场景\",\n                        \"type\":\"Item\",\n                        \"key\":\"pressureTestManage_pressureTestScene\",\n                        \"path\":\"/pressureTestManage/pressureTestScene\",\n                        \"children\":[\n                            {\n                                \"title\":\"压测场景配置\",\n                                \"type\":\"NoMenu\",\n                                \"key\":\"pressureTestManage_pressureTestScene\",\n                                \"path\":\"/pressureTestManage/pressureTestScene/pressureTestSceneConfig\"\n                            }\n                        ]\n                    },\n                    {\n                        \"title\":\"压测报告\",\n                        \"type\":\"Item\",\n                        \"key\":\"pressureTestManage_pressureTestReport\",\n                        \"path\":\"/pressureTestManage/pressureTestReport\",\n                        \"children\":[\n                            {\n                                \"title\":\"压测实况\",\n                                \"type\":\"NoMenu\",\n                                \"key\":\"pressureTestManage_pressureTestReport\",\n                                \"path\":\"/pressureTestManage/pressureTestReport/pressureTestLive\"\n                            },\n                            {\n                                \"title\":\"压测报告详请\",\n                                \"type\":\"NoMenu\",\n                                \"key\":\"pressureTestManage_pressureTestReport\",\n                                \"path\":\"/pressureTestManage/pressureTestReport/details\"\n                            }\n                        ]\n                    }\n                ]\n            }\n        ]\n    },\n    {\n        \"title\":\"巡检平台\",\n        \"key\":\"patrolScreen\",\n        \"path\":\"/accountBook\",\n        \"icon\":\"account-book\",\n        \"type\":\"SubMenu\",\n        \"children\":[\n            {\n                \"title\":\"巡检大盘\",\n                \"type\":\"Item\",\n                \"key\":\"patrolScreen\",\n                \"icon\":\"account-book\",\n                \"path\":\"/pro/e2eBigScreen\"\n            },\n            {\n                \"title\":\"巡检管理\",\n                \"path\":\"/pro/missionManage\",\n                \"type\":\"Item\",\n                \"icon\":\"account-book\",\n                \"key\":\"patrolBoard\",\n                \"children\":[\n                    {\n                        \"key\":\"patrolManage\",\n                        \"title\":\"巡检场景详情\",\n                        \"type\":\"NoMenu\",\n                        \"path\":\"/pro/missionManage/sceneDetails\"\n                    }\n                ]\n            },\n            {\n                \"title\":\"瓶颈列表\",\n                \"type\":\"Item\",\n                \"key\":\"bottleneckTable\",\n                \"icon\":\"unordered-list\",\n                \"path\":\"/pro/bottleneckTable\",\n                \"children\":[\n                    {\n                        \"title\":\"瓶颈详情\",\n                        \"key\":\"bottleneckTable\",\n                        \"type\":\"NoMenu\",\n                        \"path\":\"/pro/bottleneckTable/bottleneckDetails\"\n                    }\n                ]\n            },\n            {\n                \"title\":\"瓶颈通知\",\n                \"icon\":\"menu-unfold\",\n                \"type\":\"Item\",\n                \"key\":\"exceptionNoticeManage\",\n                \"path\":\"/pro/faultNotification\"\n            },\n            {\n                \"title\":\"配置中心\",\n                \"icon\":\"setting\",\n                \"type\":\"Item\",\n                \"path\":\"/pro/setFocus\",\n                \"key\":\"bottleneckConfig\"\n            }\n        ]\n    },\n    {\n        \"icon\":\"form\",\n        \"key\":\"tracks\",\n        \"path\":\"/tracks\",\n        \"title\":\"性能监控\",\n        \"type\":\"SubMenu\",\n        \"children\":[\n            {\n                \"key\":\"traceQuery\",\n                \"title\":\"链路查询\",\n                \"path\":\"/pro/track\",\n                \"type\":\"Item\"\n            }\n        ]\n    },\n    {\n        \"icon\":\"setting\",\n        \"title\":\"设置中心\",\n        \"path\":\"/setting\",\n        \"key\":\"configCenter\",\n        \"type\":\"SubMenu\",\n        \"children\":[\n            {\n                \"title\":\"系统管理\",\n                \"key\":\"configCenter\",\n                \"path\":\"/configCenter\",\n                \"type\":\"SubMenu\",\n                \"children\":[\n                    {\n                        \"title\":\"系统信息\",\n                        \"key\":\"system_info\",\n                        \"type\":\"Item\",\n                        \"path\":\"/configCenter/systemInfo\"\n                    },\n                    {\n                        \"title\":\"全局配置\",\n                        \"type\":\"Item\",\n                        \"key\":\"center_config\",\n                        \"path\":\"/configCenter/globalConfig\",\n                        \"children\":[\n                            {\n                                \"path\":\"\",\n                                \"title\":\"压测开关\",\n                                \"type\":\"NoMenu\",\n                                \"key\":\"configCenter_pressureMeasureSwitch\"\n                            },\n                            {\n                                \"path\":\"\",\n                                \"title\":\"白名单开关\",\n                                \"type\":\"NoMenu\",\n                                \"key\":\"configCenter_whitelistSwitch\"\n                            }\n                        ]\n                    },\n                    {\n                        \"title\":\"中间件库管理\",\n                        \"type\":\"Item\",\n                        \"key\":\"configCenter_middlewareManage\",\n                        \"path\":\"/configCenter/middlewareManage\"\n                    },\n                    {\n                        \"title\":\"开关配置\",\n                        \"type\":\"Item\",\n                        \"key\":\"configCenter_bigDataConfig\",\n                        \"path\":\"/configCenter/bigDataConfig\"\n                    },\n                    {\n                        \"path\":\"/admin\",\n                        \"title\":\"探针版本管理\",\n                        \"type\":\"Item\",\n                        \"key\":\"admins_admin\"\n                    },\n                    {\n                        \"type\":\"Item\",\n                        \"title\":\"仿真系统配置\",\n                        \"path\":\"/simulationConfig\",\n                        \"key\":\"admins_simulationConfig\"\n                    },\n                    {\n                        \"title\":\"流量账户\",\n                        \"key\":\"flowAccount\",\n                        \"type\":\"Item\",\n                        \"path\":\"/pro/flowAccount\"\n                    }\n                ]\n            },\n            {\n                \"title\":\"权限管理\",\n                \"type\":\"SubMenu\",\n                \"path\":\"/authorityManage\",\n                \"key\":\"configCenter_authorityConfig\",\n                \"children\":[\n                    {\n                        \"title\":\"权限配置中心\",\n                        \"type\":\"Item\",\n                        \"key\":\"configCenter_authorityConfig\",\n                        \"path\":\"/pro/configCenter/authorityConfig\"\n                    },\n                    {\n                        \"title\":\"操作日志\",\n                        \"type\":\"Item\",\n                        \"key\":\"configCenter_operationLog\",\n                        \"path\":\"/pro/configCenter/operationLog\"\n                    }\n                ]\n            }\n        ]\n    }\n]' WHERE `CONFIG_CODE` = 'ALL_MENU' AND `env_code` = 'system' AND `tenant_id` = -1;
END $$
DELIMITER ;
CALL add_column;
