mapper.select:
select count(1)
from t_black_list
where REDIS_KEY = #{redisKey}

SELECT t1.* FROM t_black_list t1 WHERE t1.BLIST_ID IN
#{blistId}
ORDER BY t1.BLIST_ID

select BLIST_ID,
       REDIS_KEY
from t_black_list
where use_yn = 1 and APPLICATION_ID = 0

    and tenant_id=#{tenantId}


    and env_code=#{envCode}
