mapper.select:
SELECT `status`, COUNT(*) count FROM t_application_middleware
WHERE application_id = #{applicationId}


    AND `status` IN


        #{status}



GROUP BY `status`

SELECT application_name, `status`, COUNT(*) count FROM t_application_middleware WHERE is_deleted = 0 AND application_id IN
#{applicationId}
AND `status` IN
#{status}
GROUP BY application_name, `status`
